package com.cj.faceget;import android.content.Context;import android.util.AttributeSet;/** *  An image view which always remains square with respect to its width. *  一种 保持相对于它的宽度的正方形的 视图 */public class SquaredImageView extends android.support.v7.widget.AppCompatImageView {  public SquaredImageView(Context context) {    super(context);  }  public SquaredImageView(Context context, AttributeSet attrs) {    super(context, attrs);  }  @Override  protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {    super.onMeasure(widthMeasureSpec, heightMeasureSpec);    setMeasuredDimension(getMeasuredWidth(), getMeasuredWidth());  }}